CREATE TABLE beers(
  id INT AUTO_INCREMENT NOT NULL PRIMARY KEY,
  name VARCHAR(35) NOT NULL,
  tagline VARCHAR(68) NOT NULL,
  first_brewed VARCHAR(7) NOT NULL,
  description VARCHAR(606) NOT NULL,
  image_url VARCHAR(37) NOT NULL,
  ph NUMERIC(3,1),
  brewers_tips VARCHAR(201) NOT NULL,
  contributed_by VARCHAR(24) NOT NULL
);
